import React, { useState } from 'react'
import pic1 from './img/pic-1.png'
import pic2 from './img/pic-2.png'
import pic3 from './img/pic-3.png'

export default function Popular_Product() {

    var photos = [{ pic2 }, { pic3 }]
    var img = document.querySelector("img");

    const [count, serCount] = useState(0)


    const incriment = () => {
        serCount(count + 1);


        if (count >= photos.length) {
            count = 0;
            img.src = photos[count];
        }

        else {
            img.src = photos[count];
        }

    }


    const decriment = () => {
        serCount(count - 1);


        if (count < 0) {
            count = photos.length - 1;
            img.src = photos[count];
        }

        else {
            img.src = photos[count];
        }

    }

    return (
        <div>
            <button onclick={decriment}>Previous</button>
            <img src={pic1} alt="" height="270" width="300" />
            <button onclick={incriment}>Next</button>
        </div>
    )
}
