import React, { useContext, useState, useEffect } from 'react'
import { GlobalState } from '../../../GlobalState'
import axios from 'axios'
import { Link } from 'react-router-dom'
import PaypalButton from './PaypalButton'
import './cart.css'

export default function Cart() {

    const state = useContext(GlobalState)
    const [cart, setCart] = state.userAPI.cart
    const [token] = state.token



    const [total, setTotal] = useState(0)



    useEffect(() => {
        const getTotal = () => {
            const total = cart.reduce((prev, item) => {
                return prev + (item.price * item.quantity)
            }, 0)

            setTotal(total)
        }

        getTotal()


    }, [cart])

    const addToCart = async (cart) => {
        await axios.patch('/user/addcart', { cart }, {
            headers: { Authorization: token }
        })
    }


    const increment = (id) => {
        cart.forEach(item => {
            if (item._id === id) {
                if (item.countInStock - 1 < item.quantity) {
                    alert("Out of stock");
                    return;
                }
                item.quantity += 1
            }
        })

        setCart([...cart])
        addToCart(cart)
    }

    const decrement = (id) => {
        cart.forEach(item => {
            if (item._id === id) {
                item.quantity === 1 ? item.quantity = 1 : item.quantity -= 1
            }
        })

        setCart([...cart])
        addToCart(cart)
    }

    const removeProduct = id => {
        if (window.confirm("Do you want to delete this product?")) {
            cart.forEach((item, index) => {
                if (item._id === id) {
                    cart.splice(index, 1)
                }
            })

            setCart([...cart])
            addToCart(cart)
        }
    }


    const tranSuccess = async (payment) => {

        const { paymentID, address } = payment;

        await axios.post('/api/payment', { cart, paymentID, address }, {
            headers: { Authorization: token }
        })

        setCart([])
        addToCart([])
        alert("You have successfully placed an order.")


    }


    if (cart.length === 0)
        return <h2 style={{ textAlign: "center", fontSize: "5rem" }}> Empty Cart</h2>




    return (
        <div className="full-cart">



            {
                cart.map(product => (
                    <div className=" row p-4 " key={product._id}>
                        {/* detail cart */}
                        <div className='col d-flex justify-content-center'>
                            <img src={product.images.url} alt="" className="img-edit" />
                        </div>
                        <div className="col d-flex justify-content-center align-items-center ">
                            {/* box-detail */}

                            <div className='row d-flex flex-column'>
                                <p>{product.title}</p>


                                <span>&#2547; {product.price} * {product.quantity} = &#2547; {product.price * product.quantity}</span>
                                <div className="amount">
                                    <button className="btn btn-danger" onClick={() => decrement(product._id)} > - </button>
                                    {/* <span>{product.quantity}</span> */}
                                    <button className="btn btn-warning" onClick={() => increment(product._id)} > + </button>
                                </div>

                                <div className="delete pt-4 fs-3"
                                    onClick={() => removeProduct(product._id)}>
                                    <i class="far fa-trash-alt"></i>
                                </div>
                            </div>

                        </div>
                    </div>

                ))
            }
            <div className="total d-flex justify-content-center align-items-center">
                <h3 className="total-text">Total: &#2547; {total}</h3>
                {/* <Link to="#!">Payment</Link> */}
                {/* <PaypalButton
                total={total}
                tranSuccess={tranSuccess} /> */}
                {/* <PaypalButton
                    total={total}
                    tranSuccess={tranSuccess} /> */}
                <button className='btn btn-dark'>
                    <Link id="btn_view" to='/shipping'>
                        Shipping
                    </Link>
                </button>

            </div>

        </div>
    )
}
