import React, { useState } from 'react'
import pic1 from './img/pic-1.png'
import pic2 from './img/pic-2.png'
import pic3 from './img/pic-3.png'
import { SliderData } from './SliderData'

const Popular_Product = ({ slides }) => {


    const [current, setCurrent] = useState(0);

    const length = slides.length;

    const nextSlide = () => {
        setCurrent(current === length - 1 ? 0 : current + 1);
    };

    const prevSlide = () => {
        setCurrent(current === 0 ? length - 1 : current - 1);
    };

    if (!Array.isArray(slides) || slides.length <= 0) {
        return null;
    }



    return (
        <section className='slider'>

            <button className='left-arrow' onClick={prevSlide} >pre </button>
            < button className='right-arrow' onClick={nextSlide} >next </button>
            {SliderData.map((slide, index) => {
                return (
                    <div
                        className={index === current ? 'slide active' : 'slide'}
                        key={index}
                    >
                        {index === current && (
                            <img src={slide.image} alt='travel image' className='image' />
                        )}
                    </div>
                );
            })}
        </section>
    )
}

export default Popular_Product;
