import React, { useContext, useState, useEffect } from 'react'
import { GlobalState } from '../../../GlobalState'
import axios from 'axios'
import { Link } from 'react-router-dom'
import PaypalButton from './PaypalButton'
import './cart.css'

export default function Cart() {

    const state = useContext(GlobalState)
    const [cart, setCart] = state.userAPI.cart
    const [token] = state.token

    const [isLogged] = state.userAPI.isLogged

    const [total, setTotal] = useState(0)

    useEffect(() => {
        const getTotal = () => {
            const total = cart.reduce((prev, item) => {
                return prev + (item.price * item.quantity)
            }, 0)

            setTotal(total)
        }

        getTotal()


    }, [cart])

    const addToCart = async (cart) => {
        await axios.patch('/user/addcart', { cart }, {
            headers: { Authorization: token }
        })
    }


    const increment = (id) => {
        cart.forEach(item => {
            if (item._id === id) {
                item.quantity += 1
            }
        })

        setCart([...cart])
        addToCart(cart)
    }

    const decrement = (id) => {
        cart.forEach(item => {
            if (item._id === id) {
                item.quantity === 1 ? item.quantity = 1 : item.quantity -= 1
            }
        })

        setCart([...cart])
        addToCart(cart)
    }

    const removeProduct = id => {
        if (window.confirm("Do you want to delete this product?")) {
            cart.forEach((item, index) => {
                if (item._id === id) {
                    cart.splice(index, 1)
                }
            })

            setCart([...cart])
            addToCart(cart)
        }
    }


    const tranSuccess = async (payment) => {

        const { paymentID, address } = payment;

        await axios.post('/api/payment', { cart, paymentID, address }, {
            headers: { Authorization: token }
        })

        setCart([])
        addToCart([])
        alert("You have successfully placed an order.")


    }


    if (cart.length === 0)
        return <h2 style={{ textAlign: "center", fontSize: "5rem" }}> Empty Cart</h2>


    const loggedRouter = () => {
        return (
            <>
                <li className="nav-item "><Link class="nav-link" to="/history">History</Link></li>
                {/* <li className="nav-item "><Link class="nav-link" to="/" onClick={logoutUser}><i class="fas fa-sign-out-alt"></i><p className="logout">Log-out</p></Link></li> */}
            </>
        )
    }

    return (
        <div className="full-cart">
            {
                isLogged ? loggedRouter() : <li className="nav-item">
                    {/* <NavLink className="nav-link" to="/login">
                                    <i class="far fa-user-circle rounded-circle"></i>
                                </NavLink> */}
                </li>
            }


            {
                cart.map(product => (
                    <div className="detail cart" key={product._id}>
                        <img src={product.images.url} alt="" />
                        <div className="box-detail">

                            <h2>{product.title}</h2>


                            <span>$ {product.price * product.quantity}</span>
                            <p>{product.description}</p>
                            <p>{product.content}</p>
                            <div className="amount">
                                <button onClick={() => decrement(product._id)} > - </button>
                                <span>{product.quantity}</span>
                                <button onClick={() => increment(product._id)} > + </button>
                            </div>

                            <div className="delete pt-4 fs-3"
                                onClick={() => removeProduct(product._id)}>
                                <i class="far fa-trash-alt"></i>
                            </div>

                        </div>
                    </div>
                ))
            }
            <div className="total">
                <h3 className="total-text">Total: $ {total}</h3>
                {/* <Link to="#!">Payment</Link> */}
                {/* <PaypalButton
                total={total}
                tranSuccess={tranSuccess} /> */}
                <PaypalButton
                    total={total}
                    tranSuccess={tranSuccess} />
            </div>

        </div>
    )
}
