                    <button onClick={addToCart} className="btn "><i class="fas fa-shopping-cart rounded-circle cart"></i></button>
                </div> */}



            {/* </div>
                </div>
            </div> */}


            <div className="overlay">
                <BtnRender product={product} deleteProduct={deleteProduct} />
            </div>




        </div>
    )
}
