import React from 'react'
import './productItem.css'
import { Link } from 'react-router-dom'
import BtnRender from './BtnRender'
import axios from 'axios'

export default function ProductItem({ product, isAdmin, token, callback, setCallback }) {

    const deleteProduct = async () => {

        try {
            const destroyImg = axios.post('/api/destroy', { public_id: product.images.public_id }, {
                headers: { Authorization: token }
            })

            const deleteProduct = axios.delete(`/api/products/${product._id}`, {
                headers: { Authorization: token }
            })

            await destroyImg
            await deleteProduct
            setCallback(!callback)

        } catch {
            alert(err.response.data.msg)
        }
    }
    return (
        <div className="product_card">
            {
                isAdmin && <input type="checkbox" checked={product.checked} />
                // onChange={() => handleCheck(product._id)} />
            }

            <div id="Offer-card" class="row py-4">
                <div class="col-md-6 col-lg-4 ">
                    <div class="card    "  >
                        <div class="inner">
                            <img src={product.images.url} alt="" className="card-img" />
                        </div>
                        <div class="card-body text-center">
                            <h2 title={product.title}>{product.title}</h2>
                            <span>${product.price}</span>
                            <p>{product.description}</p>
                        </div>
                        {/* <div className="">
                            <Link>
                                <button className="btn "><i class="fas fa-shopping-cart rounded-circle cart"></i></button>
                            </Link>

                            <Link to={`detail/${product._id}`}>
                                VIEW
                            </Link>
                        </div> */}


                    </div>
                </div>
            </div>








            <BtnRender product={product} deleteProduct={deleteProduct} />
        </div>
    )
}
