// import React from 'react'
import React, { useState } from 'react'
import './productItem.css'
import { Link } from 'react-router-dom'
import BtnRender from './BtnRender'
import axios from 'axios'


export default function ProductItem({ product, isAdmin, deleteProduct, handleCheck }) {




    const [current, setCurrent] = useState(0);

    const length = product.length;

    const nextSlide = () => {
        setCurrent(current === length - 1 ? 0 : current + 1);
    };

    const prevSlide = () => {
        setCurrent(current === 0 ? length - 1 : current - 1);
    };

    if (!Array.isArray(product) || product.length <= 0) {
        return null;
    }

    return (
        <div className="product_card">
            {
                isAdmin && <input type="checkbox" checked={product.checked}
                    onChange={() => handleCheck(product._id)} />
            }

            {/* <div id="Offer-card" className="row  ">
                <div className="col ">
                    <div className="card    "  >
                        <div className="inner">
                            <img src={product.images.url} alt="" className="card-img" />
                        </div>
                        <div className="card-body card text-center">
                            <h2 title={product.title}>{product.title}</h2>
                            <span>${product.price}</span>
                            <p>{product.description}</p>
                        </div>
                         */}
            <biv className='left-arrow' onClick={prevSlide} ><i class="fas fa-chevron-right"></i> </biv>
            < biv className='right-arrow' onClick={nextSlide} ><i class="fas fa-chevron-left"></i> </biv>
            {product.map((product, index) => {
                <div className={index === current ? 'slide active' : 'slide'}
                    key={index} >
                    {index === current && (
                        <div>
                            <div className="inner">
                                <img src={product.images.url} alt="" className="card-img  " />
                            </div>

                            <div className="card-body colam text-center">
                                <h3 title={product.title}>{product.title}</h3>
                                <span>&#2547;{product.price}</span>
                                {/* <p>{product.description}</p> */}
                            </div>
                        </div>
                    )}


                </div>

            })}

            <div className="card row ">

                {/* <div className="inner">
                    <img src={product.images.url} alt="" className="card-img  " />
                </div> */}

                {/* <div className="card-body colam text-center">
                    <h3 title={product.title}>{product.title}</h3>
                    <span>&#2547;{product.price}</span> */}
                {/* <p>{product.description}</p> */}
                {/* </div> */}
                {/* <div className="overlay">
                    <button onClick={addToCart} className="btn "><i class="fas fa-shopping-cart rounded-circle cart"></i></button>
                </div> */}



                {/* </div>
                </div>
            </div> */}


                <div className="overlay">
                    <BtnRender product={product} deleteProduct={deleteProduct} />
                </div>
            </div>




        </div>
    )
}
